
var request = require('request')

module.exports = function(string, cb){
    if(string != ''){
        request('https://en.wikipedia.org/w/api.php?action=opensearch&search='+string, function(err, e, body){
            if(err) cb(err, null)
            var obj = JSON.parse('{"data":DATA}'.replace('DATA', body))
            cb(null, obj.data[2][0])
        })
    }
}
